import{_ as e,c as t,o,b as l}from"./app.8beebb82.js";const v='{"title":"\u9762\u8BD5\u9898\u5206\u6790","description":"","frontmatter":{"title":"\u9762\u8BD5\u9898\u5206\u6790"},"headers":[{"level":3,"title":"1. \u7EC4\u4EF6\u901A\u4FE1\u65B9\u5F0F","slug":"_1-\u7EC4\u4EF6\u901A\u4FE1\u65B9\u5F0F"},{"level":3,"title":"React \u7EC4\u4EF6","slug":"react-\u7EC4\u4EF6"},{"level":3,"title":"2. \u6D4F\u89C8\u5668\u8F93\u5165URL\u53D1\u751F\u4E86\u4EC0\u4E48\uFF1F","slug":"_2-\u6D4F\u89C8\u5668\u8F93\u5165url\u53D1\u751F\u4E86\u4EC0\u4E48\uFF1F"}],"relativePath":"interview/summary/index.md","lastUpdated":1650999786043}',i={},r=l('<h3 id="_1-\u7EC4\u4EF6\u901A\u4FE1\u65B9\u5F0F" tabindex="-1">1. \u7EC4\u4EF6\u901A\u4FE1\u65B9\u5F0F <a class="header-anchor" href="#_1-\u7EC4\u4EF6\u901A\u4FE1\u65B9\u5F0F" aria-hidden="true">#</a></h3><p>\u7EC4\u4EF6\u7684\u901A\u4FE1\u65B9\u5F0F\u5206\u4E3A\u4E24\u5927\u7C7B\uFF0C\u4E00\u7C7B\u662F <strong>\u7236\u5B50\u7EC4\u4EF6\u901A\u4FE1</strong>\uFF0C\u53E6\u4E00\u7C7B\u662F <strong>\u4EFB\u4F55\u5173\u7CFB\u7C7B\u578B\u7EC4\u4EF6\u901A\u4FE1\uFF08\u7236\u5B50\u3001\u5144\u5F1F\u3001\u975E\u5144\u5F1F\uFF09</strong></p><h4 id="vue-\u7EC4\u4EF6" tabindex="-1">Vue \u7EC4\u4EF6 <a class="header-anchor" href="#vue-\u7EC4\u4EF6" aria-hidden="true">#</a></h4><ol><li><p>\u7236\u5B50\u7EC4\u4EF6\u7684\u901A\u4FE1\u65B9\u5F0F\uFF1A</p><ul><li><p>\u7236 \u27A1\uFE0F \u5B50\uFF1A\u901A\u8FC7\u7ED9\u5B50\u7EC4\u4EF6\u6DFB\u52A0 <strong>\u81EA\u5B9A\u4E49\u5C5E\u6027</strong>\uFF0C\u6BD4\u5982\uFF1A<code>&lt;List :list=&quot;list&quot;/&gt;</code>\uFF0Clist\u662F\u7236\u7EC4\u4EF6\u7ED9\u5B50\u7EC4\u4EF6\u4F20\u9012\u7684\u6570\u636E\u3002\u5B50\u83B7\u53D6\u7236\u7684\u6570\u636E\uFF0C\u5728\u5B50\u7EC4\u4EF6\u4E2D\u4F7F\u7528props\u5C5E\u6027\u83B7\u53D6</p></li><li><p>\u5B50 \u27A1\uFE0F \u7236\uFF1A\u5728\u5B50\u7EC4\u4EF6\u901A\u8FC7 <code>emit</code> \u89E6\u53D1\u81EA\u5B9A\u4E49\u65B9\u6CD5\uFF0C\u5728\u7236\u7EC4\u4EF6\u76D1\u542C\u6B64\u65B9\u6CD5\u5E76\u5B9E\u73B0\u5BF9\u5E94\u7684\u5904\u7406\u51FD\u6570\uFF0C\u6BD4\u5982\uFF1A\u7236\u7EC4\u4EF6\u4E2D\u6709 <code>&lt;List @delete=&quot;deleteHandler&quot;/&gt;</code> \u7EC4\u4EF6\uFF0C<code>deleteHandler</code> \u5C31\u662F\u5728\u7236\u7EC4\u4EF6\u4E2D\u7684\u58F0\u660E\u7684\u5904\u7406\u51FD\u6570\uFF0C\u5728\u5B50\u7EC4\u4EF6\u4E2D\u901A\u8FC7 <code>this.$emit(&#39;delete&#39;, \u6570\u636E)</code>\uFF08vue3 composition-api \u4E2D\u76F4\u63A5\u4F7F\u7528 emit \u51FD\u6570\uFF0C\u4E0D\u518D\u4F7F\u7528 this\uFF09\uFF0C\u4ECE\u800C\u8C03\u7528\u7236\u7EC4\u4EF6\u7684\u65B9\u6CD5\uFF0C\u5E76\u628A\u6570\u636E\u4F20\u9012\u5230\u7236\u7EC4\u4EF6</p></li><li><p>props \u662F\u53EA\u8BFB\u5C5E\u6027\uFF0C\u4E0D\u53EF\u4EE5\u88AB\u4FEE\u6539\uFF0C\u6240\u6709\u88AB\u4FEE\u6539\u90FD\u4F1A\u5931\u6548\u4E14\u62A5\u8B66\u544A</p></li></ul></li><li><p>\u4EFB\u4F55\u5173\u7CFB\u7C7B\u578B\u7EC4\u4EF6\u901A\u4FE1\uFF08\u7236\u5B50\u3001\u5144\u5F1F\u3001\u975E\u5144\u5F1F\uFF09\u65B9\u5F0F\uFF1A</p><ul><li><p><code>EventBus</code>\uFF08<strong>\u7A0D\u5FAE\u5927\u4E00\u70B9\u7684\u7CFB\u7EDF\u4E0D\u63A8\u8350</strong>\uFF09\uFF1A\u4F7F\u7528\u65B9\u6CD5\u662F\u521B\u5EFA\u4E00\u4E2A\u65B0\u7684Vue\u5B9E\u4F8B\uFF0C\u9700\u8981\u901A\u4FE1\u7684\u7EC4\u4EF6\u90FD\u5F15\u5165\u8BE5Vue\u5B9E\u4F8B\uFF0C\u4F20\u9012\u6570\u636E\u7684\u7EC4\u4EF6\u4F7F\u7528 <code>event.$emit(&#39;\u540D\u79F0&#39;,\u53C2\u6570)</code> \u53D1\u9001\u6570\u636E\uFF0C\u63A5\u6536\u6570\u636E\u7684\u7EC4\u4EF6\u4F7F\u7528 <code>event.$on(&#39;\u540D\u79F0&#39;,\u65B9\u6CD5)</code> \u63A5\u6536\u6570\u636E</p></li><li><p><code>VueX\uFF08Pinia\uFF09</code>\uFF1A\u96C6\u4E2D\u7BA1\u7406\u9879\u76EE\u516C\u5171\u6570\u636E\uFF0CVuex \u7684\u72B6\u6001\u5B58\u50A8\u662F\u54CD\u5E94\u5F0F\u7684\uFF0C\u5F53 Vue \u7EC4\u4EF6\u4ECE store \u4E2D\u8BFB\u53D6\u72B6\u6001\u7684\u65F6\u5019\uFF0C\u82E5 store \u4E2D\u7684\u72B6\u6001\u53D1\u751F\u53D8\u5316\uFF0C\u90A3\u4E48\u76F8\u5E94\u7684\u7EC4\u4EF6\u4E5F\u4F1A\u76F8\u5E94\u5730\u5F97\u5230\u9AD8\u6548\u66F4\u65B0\u3002\u4E0D\u80FD\u76F4\u63A5\u6539\u53D8 store \u4E2D\u7684\u72B6\u6001\u3002\u6539\u53D8 store \u4E2D\u7684\u72B6\u6001\u7684\u552F\u4E00\u9014\u5F84\u5C31\u662F\u663E\u5F0F\u5730\u63D0\u4EA4 (commit) <code>mutation</code></p></li><li><p><code>$root</code> / <code>$parent</code> / <code>$children</code>\uFF08<strong>\u4E0D\u63A8\u8350</strong>\uFF09\uFF1A\u901A\u8FC7\u62FF\u5230 <code>\u6839\u7EC4\u4EF6/\u7236\u7EC4\u4EF6/\u5B50\u7EC4\u4EF6</code> \u7684\u5B9E\u4F8B\uFF0C\u7136\u540E\u76F4\u63A5\u53BB\u64CD\u4F5C\u83B7\u53D6\u6570\u636E\u6216\u8C03\u7528\u8BE5\u7EC4\u4EF6\u7684\u65B9\u6CD5</p></li></ul></li></ol><p>EventBus\u7684\u4F18\u7F3A\u70B9\uFF1A</p><ul><li><p>\u7F3A\u70B9\uFF1Avue\u662F\u5355\u9875\u5E94\u7528\uFF0C\u5982\u679C\u4F60\u5728\u67D0\u4E00\u4E2A\u9875\u9762\u5237\u65B0\u4E86\u4E4B\u540E\uFF0C\u4E0E\u4E4B\u76F8\u5173\u7684EventBus\u4F1A\u88AB\u79FB\u9664\uFF0C\u8FD9\u6837\u5C31\u5BFC\u81F4\u4E1A\u52A1\u8D70\u4E0D\u4E0B\u53BB\u3002\u540C\u65F6\u5982\u679C\u9875\u9762\u4E2D\u6709\u53CD\u590D\u64CD\u4F5C\u7684\u4E1A\u52A1\uFF0CEventBus\u5728\u76D1\u542C\u7684\u65F6\u5019\u5C31\u4F1A\u89E6\u53D1\u5F88\u591A\u6B21\uFF0C\u9700\u8981\u597D\u597D\u5904\u7406EventBus\u5728\u9879\u76EE\u4E2D\u7684\u5173\u7CFB\u3002\u5728vue\u9875\u9762\u9500\u6BC1\u65F6\uFF0C\u540C\u65F6\u79FB\u9664EventBus\u4E8B\u4EF6\u76D1\u542C\uFF0C\u5426\u5219\u5BB9\u6613\u9020\u6210\u5185\u5B58\u6CC4\u6F0F</p></li><li><p>\u4F18\u70B9\uFF1A\u89E3\u51B3\u4E86\u591A\u5C42\u7EC4\u4EF6\u4E4B\u95F4\u7E41\u7410\u7684\u4E8B\u4EF6\u4F20\u64AD\uFF0C\u4F7F\u7528\u539F\u7406\u5341\u5206\u7B80\u5355\uFF0C\u4EE3\u7801\u91CF\u5C11\u3002\u9002\u5408\u4E1A\u52A1\u7B80\u5355\uFF0C\u7EC4\u4EF6\u4F20\u9012\u6570\u636E\u8F83\u5C11\u7684\u9879\u76EE\uFF0C\u5927\u578B\u9879\u76EE\u4E1A\u52A1\u590D\u6742\u7684\u8FD8\u662F\u5C3D\u91CF\u4F7F\u7528VueX\uFF08Pinia\uFF09</p></li></ul><h3 id="react-\u7EC4\u4EF6" tabindex="-1">React \u7EC4\u4EF6 <a class="header-anchor" href="#react-\u7EC4\u4EF6" aria-hidden="true">#</a></h3><ol><li><p>\u7236\u5B50\u7EC4\u4EF6\uFF1A</p><ul><li><p>\u7236 \u27A1\uFE0F \u5B50\uFF1A\u901A\u8FC7 props \u81EA\u5B9A\u4E49\u5C5E\u6027\u4F20\u9012\u7ED9\u5B50\u7EC4\u4EF6</p></li><li><p>\u5B50 \u27A1\uFE0F \u7236\uFF1A</p><ul><li>\u901A\u8FC7\u7236\u7EC4\u4EF6 props \u81EA\u5B9A\u4E49\u65B9\u6CD5\uFF0C\u5728\u5B50\u7EC4\u4EF6\u4E2D\u8C03\u7528\u5BF9\u5E94\u7684\u65B9\u6CD5\uFF0C\u7136\u540E\u901A\u8FC7\u53C2\u6570\u4F20\u9012\u7ED9\u7236\u7EC4\u4EF6</li><li>\u5728\u7236\u7EC4\u4EF6\u5B9A\u4E49 ref\uFF0C\u4F20\u9012\u7ED9\u5B50\u7EC4\u4EF6\uFF08\u5B50\u7EC4\u4EF6\u662F\u51FD\u6570\u7EC4\u4EF6\u5C31\u9700\u8981 <a href="https://juejin.cn/post/6987045486158938149#heading-9" target="_blank" rel="noopener noreferrer">ref \u8F6C\u53D1</a>\u6765\u5B9E\u73B0\u4E86\uFF09\uFF0C\u5728\u5B50\u7EC4\u4EF6\u5B9E\u73B0\u65B9\u6CD5\uFF0C\u7236\u7EC4\u4EF6\u76F4\u63A5\u901A\u8FC7 ref \u5B9E\u4F8B\u8C03\u7528\u5B50\u7EC4\u4EF6\u7684\u65B9\u6CD5\u83B7\u53D6\u6570\u636E</li></ul></li></ul></li><li><p>\u5144\u5F1F\u7EC4\u4EF6\uFF1A</p><p>\u82E5\u4E24\u4E2A\u7EC4\u4EF6\u5171\u7528\u4E00\u4E2A\u7236\u7EC4\u4EF6\uFF0C\u90A3\u4E48\u6211\u4EEC\u53EF\u4EE5\u8003\u8651\u628A\u5B83\u4EEC\u7684\u5171\u4EAB\u6570\u636E\u63D0\u5347\u4FDD\u5B58\u5728\u5B83\u4EEC\u7684\u7236\u7EC4\u4EF6\u4E2D\uFF0C\u5E76\u5B9A\u4E49\u5BF9\u5E94\u7684\u65B9\u6CD5\u6765\u89E6\u53D1\u6570\u636E\u53D8\u66F4\uFF0C\u5E76\u5C06\u6570\u636E\u53CA\u66F4\u65B0\u65B9\u6CD5\u5206\u522B\u4F20\u9012\u7ED9\u4E24\u4E2A\u5144\u5F1F\u5B50\u7EC4\u4EF6\u5373\u53EF</p></li><li><p>\u4EFB\u4F55\u5173\u7CFB\u7C7B\u578B\u7684\u7EC4\u4EF6\uFF1A</p><ul><li><p><code>Context</code> \u4E0A\u4E0B\u6587\uFF1A\u5F53\u67D0\u4E9B\u6570\u636E\u5168\u5C40\u6216\u90E8\u5206\u5168\u5C40\u5171\u4EAB\u65F6\uFF0C\u800C\u4E14\u53EF\u80FD\u662F\u67D0\u8282\u70B9\u8DDF\u5B83\u5F88\u6DF1\u7684\u4E00\u4E2A\u7EC4\u4EF6\u624D\u4F1A\u5171\u4EAB\u65F6\uFF0C\u5C31\u53EF\u4EE5\u8003\u8651\u5728\u8FD9\u4E2A\u8282\u70B9\u4E0A\u58F0\u660E\u4E00\u4E2A\u4E0A\u4E0B\u6587\u5171\u4EAB\u6570\u636E\uFF08\u5426\u5219\u7528 props \u4E00\u5C42\u4E00\u5C42\u5F80\u4E0B\u4F20\u5C31\u7279\u522B\u590D\u6742\uFF09\u3002\u901A\u8FC7 <a href="https://juejin.cn/post/6985104812232671268" target="_blank" rel="noopener noreferrer"><code>React.createContext(defaultValue)</code> \u6765\u521B\u5EFA\u4E0A\u4E0B\u6587\u6570\u636E</a></p></li><li><p><code>Redux</code>\uFF1A\u5168\u5C40\u72B6\u6001\u7BA1\u7406\u5E93\uFF0C\u4EFB\u4F55\u4E00\u4E2A\u7EC4\u4EF6\u90FD\u53EF\u4F7F\u7528\u5176\u4E2D\u5B58\u50A8\u7684\u6570\u636E\uFF0C\u5E76\u901A\u8FC7\u56FA\u5B9A\u7684 dispatch \u65B9\u6CD5\u53BB\u66F4\u65B0\u6570\u636E \u27A1\uFE0F <a href="https://juejin.cn/post/6990664042402545672" target="_blank" rel="noopener noreferrer">\u4F7F\u7528\u8BE6\u60C5\u53EF\u89C1</a></p></li></ul></li></ol><h3 id="_2-\u6D4F\u89C8\u5668\u8F93\u5165url\u53D1\u751F\u4E86\u4EC0\u4E48\uFF1F" tabindex="-1">2. \u6D4F\u89C8\u5668\u8F93\u5165URL\u53D1\u751F\u4E86\u4EC0\u4E48\uFF1F <a class="header-anchor" href="#_2-\u6D4F\u89C8\u5668\u8F93\u5165url\u53D1\u751F\u4E86\u4EC0\u4E48\uFF1F" aria-hidden="true">#</a></h3><div class="tip custom-block"><p class="custom-block-title">\u5173\u952E\u70B9</p><p>DNS\u89E3\u6790\u3001TCP\u63E1\u624B\u3001HTTP\u7F13\u5B58\u3001\u91CD\u5B9A\u5411\u3001\u670D\u52A1\u5668\u72B6\u6001\u7801\u3001\u6E32\u67D3\u5F15\u64CE\u548CJS\u5F15\u64CE\u4E92\u65A5\u3001\u6E32\u67D3\u8FC7\u7A0B\u3001\u6D4F\u89C8\u5668\u8FDB\u7A0B\u3001\u7F51\u8DEF\u8FDB\u7A0B\u3001\u6E32\u67D3\u8FDB\u7A0B</p></div>',10),a=[r];function d(p,c,n,s,u,h){return o(),t("div",null,a)}var f=e(i,[["render",d]]);export{v as __pageData,f as default};
