import{_ as e,c as o,o as c,b as d}from"./app.6fdcbe56.js";const S=JSON.parse('{"title":"\u9762\u8BD5\u9898\u5206\u6790\uFF08\u4E8C\uFF09","description":"","frontmatter":{"title":"\u9762\u8BD5\u9898\u5206\u6790\uFF08\u4E8C\uFF09"},"headers":[{"level":2,"title":"1. webpack \u4E0E rollup \u7684\u533A\u522B\uFF1Fwebpack5 \u4E0E webpack4\uFF1F","slug":"_1-webpack-\u4E0E-rollup-\u7684\u533A\u522B\uFF1Fwebpack5-\u4E0E-webpack4\uFF1F"},{"level":2,"title":"2. composition-api \u4E0E hooks \u7684\u5F02\u540C\u70B9\uFF1F","slug":"_2-composition-api-\u4E0E-hooks-\u7684\u5F02\u540C\u70B9\uFF1F"},{"level":2,"title":"3. vue \u4E2D\u7684 jsx \u4E0E react \u7684 jsx \u533A\u522B\uFF1F","slug":"_3-vue-\u4E2D\u7684-jsx-\u4E0E-react-\u7684-jsx-\u533A\u522B\uFF1F"},{"level":2,"title":"4. \u8BF4\u8BF4 XSS \u653B\u51FB\u4E0E\u9632\u8303\uFF1F","slug":"_4-\u8BF4\u8BF4-xss-\u653B\u51FB\u4E0E\u9632\u8303\uFF1F"},{"level":2,"title":"5. DNS \u52AB\u6301","slug":"_5-dns-\u52AB\u6301"},{"level":2,"title":"6. \u8BF4\u8BF4 cors \u8DE8\u7AD9\u811A\u672C\u653B\u51FB\u9632\u8303\uFF1F","slug":"_6-\u8BF4\u8BF4-cors-\u8DE8\u7AD9\u811A\u672C\u653B\u51FB\u9632\u8303\uFF1F"},{"level":2,"title":"7. \u8BF4\u8BF4 CSRF \u8DE8\u7AD9\u8BF7\u6C42\u4F2A\u9020\u9632\u8303\uFF1F","slug":"_7-\u8BF4\u8BF4-csrf-\u8DE8\u7AD9\u8BF7\u6C42\u4F2A\u9020\u9632\u8303\uFF1F"},{"level":2,"title":"8. devOps\uFF08\u5982 jenkins \u7B49\uFF09","slug":"_8-devops\uFF08\u5982-jenkins-\u7B49\uFF09"},{"level":2,"title":"9. \u6D4B\u8BD5\u6846\u67B6\uFF08jest\uFF09","slug":"_9-\u6D4B\u8BD5\u6846\u67B6\uFF08jest\uFF09"},{"level":2,"title":"10. \u8BF4\u4E00\u8BF4 qiankun \u7684\u8DEF\u7531\u5339\u914D\u539F\u7406","slug":"_10-\u8BF4\u4E00\u8BF4-qiankun-\u7684\u8DEF\u7531\u5339\u914D\u539F\u7406"}],"relativePath":"interview/summary/strands.md"}'),i={name:"interview/summary/strands.md"},l=d('<h2 id="_1-webpack-\u4E0E-rollup-\u7684\u533A\u522B\uFF1Fwebpack5-\u4E0E-webpack4\uFF1F" tabindex="-1">1. webpack \u4E0E rollup \u7684\u533A\u522B\uFF1Fwebpack5 \u4E0E webpack4\uFF1F <a class="header-anchor" href="#_1-webpack-\u4E0E-rollup-\u7684\u533A\u522B\uFF1Fwebpack5-\u4E0E-webpack4\uFF1F" aria-hidden="true">#</a></h2><p>\u89C1 <a href="/knowledge/engineering/compare.html">\u6253\u5305\u5DE5\u5177\u5BF9\u6BD4 \u{1F517}</a></p><h2 id="_2-composition-api-\u4E0E-hooks-\u7684\u5F02\u540C\u70B9\uFF1F" tabindex="-1">2. composition-api \u4E0E hooks \u7684\u5F02\u540C\u70B9\uFF1F <a class="header-anchor" href="#_2-composition-api-\u4E0E-hooks-\u7684\u5F02\u540C\u70B9\uFF1F" aria-hidden="true">#</a></h2><p>react \u5BF9 hook \u7684\u5B9A\u4E49\u662F\uFF1A</p><blockquote><p>\u5B83\u53EF\u4EE5\u8BA9\u4F60\u5728\u4E0D\u7F16\u5199 class \u7684\u60C5\u51B5\u4E0B\uFF0C\u8BA9\u4F60\u5728\u51FD\u6570\u7EC4\u4EF6\u91CC \u201C\u94A9\u5165\u201D React state \u53CA\u751F\u547D\u5468\u671F\u7B49\u7279\u6027\u7684\u51FD\u6570</p></blockquote><p>vue \u7684 composition-api\uFF08\u6211\u4EEC\u4E5F\u53EF\u79F0\u4E3A <code>Vue Hooks</code>\uFF09\uFF0C\u5B83\u53D7\u5230\u4E86 React Hooks \u7684\u542F\u53D1\uFF0C\u4F46\u4E5F\u6709\u4E9B\u4E0D\u540C\uFF0C\u89C4\u907F\u4E86\u4E00\u4E9B react \u7684\u95EE\u9898</p><p>\u5728 hook \u601D\u60F3\u51FA\u6765\u4E4B\u524D\uFF0C\u9488\u5BF9\u4E1A\u52A1\u4E0A\u7684\u903B\u8F91\u590D\u7528\u529F\u80FD\uFF1Avue \u91C7\u7528 <code>mixins</code> \u65B9\u6848\uFF0Creact \u91C7\u7528 <code>render props</code>\u3001<code>\u9AD8\u9636\u7EC4\u4EF6</code> \u7684\u65B9\u6848\u5904\u7406</p><ul><li>mixins \u4E0E\u7EC4\u4EF6\u4E4B\u524D\u662F\u9690\u5F0F\u4F9D\u8D56\uFF0C\u53EF\u80FD\u4F1A\u4EA7\u751F\u51B2\u7A81\uFF08\u6BD4\u5982\u6211\u4EEC\u5B9A\u4E49\u7684\u65B9\u6CD5\u540D\u3001\u6570\u636E\u540D\u91CD\u540D\u7684\u60C5\u51B5\uFF09</li><li>\u9AD8\u9636\u7EC4\u4EF6\u548C <code>render props</code> \u5BB9\u6613\u5BFC\u81F4\u7EC4\u4EF6\u5D4C\u5957\u5C42\u6B21\u8FC7\u6DF1\uFF0C\u589E\u5927\u590D\u6742\u5EA6\u548C\u7EF4\u62A4\u6210\u672C</li></ul><p>\u4E0D\u540C\u70B9\uFF1A</p><ol><li><p>React Hooks \u662F\u57FA\u4E8E <code>\u94FE\u8868</code> \u5B9E\u73B0\u7684\uFF0C\u4E0D\u80FD\u5728\u5FAA\u73AF\u5185\u90E8\u3001\u6761\u4EF6\u8BED\u53E5\u4E2D\u6216\u5D4C\u5957\u51FD\u6570\u91CC\u8C03\u7528 Hooks\uFF1BVue Hooks \u5B9A\u4E49\u5728 <code>setup</code> \u51FD\u6570\u4E2D\uFF0C\u7531\u4E8E\u5B83\u6570\u636E\u7684\u54CD\u5E94\u5F0F\u662F\u57FA\u4E8E <a href="https://developer.mozilla.org/zh-CN/docs/Web/JavaScript/Reference/Global_Objects/Proxy" target="_blank" rel="noreferrer">Proxy</a> \u5B9E\u73B0\u7684\uFF0C\u53EA\u8981\u66F4\u6539\u6570\u636E\u5C31\u4F1A\u89E6\u53D1\u5BF9\u5E94\u7684\u4F9D\u8D56\u51FD\u6570\u6267\u884C\u66F4\u65B0\u64CD\u4F5C\uFF0C\u6240\u4EE5\u907F\u5F00\u4E86 react \u53EF\u80FD\u9047\u5230\u7684\u6027\u80FD\u95EE\u9898</p></li><li><p>\u4EE3\u7801\u6267\u884C\u65F6\u673A\uFF1AReact hooks \u5728\u7EC4\u4EF6\u6BCF\u6B21\u6E32\u67D3\u65F6\u90FD\u4F1A\u8FD0\u884C\uFF1BVue Hooks \u5B9A\u4E49\u5728 <code>setup()</code> \u51FD\u6570\u4E2D\uFF0C\u5B83\u4F1A\u65E9\u4E8E <code>beforeCreate</code> \u548C <code>created</code> \u751F\u547D\u5468\u671F\u51FD\u6570\uFF0C\u4E14\u4EC5\u6267\u884C\u4E00\u6B21</p></li><li><p>React \u4F7F\u7528 <code>useState</code> Hook \u6765\u58F0\u660E\u6570\u636E\uFF0C\u4F20\u5165\u4E00\u4E2A\u53C2\u6570\u4F5C\u4E3A\u6570\u636E\u521D\u59CB\u503C\uFF08\u53EA\u4F1A\u5728\u521D\u6B21\u8FD0\u884C\u8FD9\u4E2A\u51FD\u6570\u65F6\u4F7F\u7528\u8FD9\u4E2A\u503C\u8FDB\u884C\u521D\u59CB\u5316\uFF09\uFF1BVue \u4E2D\u4F7F\u7528 <code>ref</code> \u548C <code>reactive</code> \u8FD9\u4E24\u4E2A\u51FD\u6570\u6765\u5B9A\u4E49\u6570\u636E</p></li></ol><h2 id="_3-vue-\u4E2D\u7684-jsx-\u4E0E-react-\u7684-jsx-\u533A\u522B\uFF1F" tabindex="-1">3. vue \u4E2D\u7684 jsx \u4E0E react \u7684 jsx \u533A\u522B\uFF1F <a class="header-anchor" href="#_3-vue-\u4E2D\u7684-jsx-\u4E0E-react-\u7684-jsx-\u533A\u522B\uFF1F" aria-hidden="true">#</a></h2><p>vue \u7EC4\u4EF6\u4F7F\u7528 jsx \u9700\u8981\u4F7F\u7528\u63D2\u4EF6\u8FDB\u884C\u7F16\u8BD1\uFF0Creact \u7EC4\u4EF6\u4E2D\u662F\u9ED8\u8BA4\u652F\u6301\u7684</p><p>vue template \u4E0E react jsx \u7684\u533A\u522B\uFF1A</p><p>vue \u4E2D\u63D0\u4F9B\u4E00\u7CFB\u5217\u6307\u4EE4\u5E2E\u52A9\u6211\u4EEC\u6784\u5EFA\u9875\u9762</p><h2 id="_4-\u8BF4\u8BF4-xss-\u653B\u51FB\u4E0E\u9632\u8303\uFF1F" tabindex="-1">4. \u8BF4\u8BF4 XSS \u653B\u51FB\u4E0E\u9632\u8303\uFF1F <a class="header-anchor" href="#_4-\u8BF4\u8BF4-xss-\u653B\u51FB\u4E0E\u9632\u8303\uFF1F" aria-hidden="true">#</a></h2><p><code>XSS</code> \u653B\u51FB\uFF08<code>Cross-Site Scripting</code>\uFF0C\u8DE8\u7AD9\u811A\u672C\u653B\u51FB\uFF09\uFF0C\u5B83\u662F\u9875\u9762\u4E2D\u88AB\u6CE8\u5165\u4E86\u6076\u610F\u7684\u4EE3\u7801</p><p>\u4E00\u822C\uFF0CXSS \u653B\u51FB\u6709\u4E0B\u5217\u6CE8\u5165\u65B9\u5F0F\uFF1A</p><ul><li>\u5728 HTML \u4E2D\u5185\u5D4C\u7684\u6587\u672C\u4E2D\uFF0C\u6076\u610F\u5185\u5BB9\u4EE5 script \u6807\u7B7E\u5F62\u6210\u6CE8\u5165</li><li>\u5728\u5185\u8054\u7684 JavaScript \u4E2D\uFF0C\u62FC\u63A5\u7684\u6570\u636E\u7A81\u7834\u4E86\u539F\u672C\u7684\u9650\u5236\uFF08\u5B57\u7B26\u4E32\uFF0C\u53D8\u91CF\uFF0C\u65B9\u6CD5\u540D\u7B49\uFF09</li><li>\u5728\u6807\u7B7E\u5C5E\u6027\u4E2D\uFF0C\u6076\u610F\u5185\u5BB9\u5305\u542B\u5F15\u53F7\uFF0C\u4ECE\u800C\u7A81\u7834\u5C5E\u6027\u503C\u7684\u9650\u5236\uFF0C\u6CE8\u5165\u5176\u4ED6\u5C5E\u6027\u6216\u8005\u6807\u7B7E</li><li>\u5728\u6807\u7B7E\u7684 href\u3001src \u7B49\u5C5E\u6027\u4E2D\uFF0C\u5305\u542B <code>javascript:</code> \u7B49\u53EF\u6267\u884C\u4EE3\u7801</li><li>\u5728 onload\u3001onerror\u3001onclick \u7B49\u4E8B\u4EF6\u4E2D\uFF0C\u6CE8\u5165\u4E0D\u53D7\u63A7\u5236\u4EE3\u7801</li><li>\u5728 style \u5C5E\u6027\u548C\u6807\u7B7E\u4E2D\uFF0C\u5305\u542B\u7C7B\u4F3C <code>background-image:url(&quot;javascript:...&quot;);</code> \u7684\u4EE3\u7801\uFF08\u65B0\u7248\u672C\u6D4F\u89C8\u5668\u5DF2\u7ECF\u53EF\u4EE5\u9632\u8303\uFF09</li><li>\u5728 style \u5C5E\u6027\u548C\u6807\u7B7E\u4E2D\uFF0C\u5305\u542B\u7C7B\u4F3C <code>expression(...)</code> \u7684 CSS \u8868\u8FBE\u5F0F\u4EE3\u7801\uFF08\u65B0\u7248\u672C\u6D4F\u89C8\u5668\u5DF2\u7ECF\u53EF\u4EE5\u9632\u8303\uFF09</li></ul><p>\u9632\u8303\u624B\u6BB5\uFF08\u53EF\u80FD\u5E76\u4E0D\u5168\u9762\uFF09\uFF1A</p><ul><li><p><code>\u8F93\u5165\u8FC7\u6EE4</code>\uFF0C\u8FD9\u4E5F\u4EC5\u9650\u4E8E\u660E\u786E\u7C7B\u578B\u7684\u7528\u6237\u8F93\u5165\uFF0C\u6BD4\u5982 <code>\u6570\u5B57</code>\u3001<code>URL</code>\u3001<code>\u90AE\u7BB1</code> \u7B49\u5185\u5BB9\u8FDB\u884C\u5FC5\u8981\u7684\u8FC7\u6EE4\u5904\u7406</p></li><li><p><code>\u8F93\u5165\u957F\u5EA6\u9650\u5236</code>\uFF0C\u867D\u4E0D\u80FD\u5B8C\u5168\u9632\u6B62\uFF0C\u4F46\u53EF\u4EE5\u589E\u52A0 XSS \u653B\u51FB\u7684\u96BE\u5EA6</p></li><li><p><code>\u505A\u7EAF\u524D\u7AEF\u6E32\u67D3</code>\uFF0C\u5C06\u6570\u636E\u4E0E\u4EE3\u7801\u5206\u9694\u5F00\uFF0C\u5BF9 HTML \u505A\u5145\u5206 <code>\u8F6C\u4E49</code>\uFF08\u8F93\u51FA HTML \u65F6\u8FDB\u884C\uFF09</p></li><li><p>\u7EAF\u524D\u7AEF\u6E32\u67D3\u65B9\u6848\u4E2D\uFF0C\u5C3D\u91CF\u91C7\u7528 <code>.innerText</code>\u3001<code>.textContent</code>\u3001<code>.setAttribute</code> \u7B49 API\uFF0C\u907F\u514D\u4F7F\u7528 <code>.innerHTML</code>\u3001<code>.outerHTML</code>\u3001<code>.document.write()</code> \u7B49 API\uFF1B\u5728 vue/react \u6846\u67B6\u4E2D\uFF0C\u907F\u514D\u4F7F\u7528 <code>v-html</code>/<code>dangerouslySetInnerHTML</code> \u529F\u80FD\uFF1B\u6CE8\u610F\u5173\u6CE8 DOM \u5185\u8054\u4E8B\u4EF6\u6216 <code>&lt;a&gt;</code> \u6807\u7B7E\u7684 <code>href</code> \u5C5E\u6027\uFF0Cjs \u7684 <code>eval()</code>\u3001<code>setTimeout()</code>\u3001<code>setImmediate()</code>\u3001<code>setInterval()</code> \u7B49 API \u5747\u80FD\u5C06 <strong>\u5B57\u7B26\u4E32</strong> \u4F5C\u4E3A\u4EE3\u7801\u76F4\u63A5\u6267\u884C</p></li></ul><h2 id="_5-dns-\u52AB\u6301" tabindex="-1">5. DNS \u52AB\u6301 <a class="header-anchor" href="#_5-dns-\u52AB\u6301" aria-hidden="true">#</a></h2><p>\u57DF\u540D\u89E3\u6790\u9700\u8981\u7531\u4E13\u95E8\u7684\u57DF\u540D\u89E3\u6790\u670D\u52A1\u5668\u6765\u5B8C\u6210\uFF0C<code>DNS</code> \u5C31\u662F\u8FDB\u884C\u57DF\u540D\u89E3\u6790\u7684\u670D\u52A1\u5668\uFF08<code>Domain Name System</code> \u6216<code>Domain Name Service</code>\uFF09</p><p>\u5728\u7F51\u7EDC\u4E2D\uFF0C\u673A\u5668\u4E4B\u95F4\u662F\u901A\u8FC7 <code>ip</code> \u5730\u5740\u4E92\u76F8\u8BBF\u95EE\u4E0E\u901A\u4FE1\u7684\uFF0C\u4F46\u662F\u4E3A\u4E86\u4FBF\u4E8E\u8BB0\u5FC6\uFF0C\u5C31\u53EF\u4EE5\u4E3A\u6BCF\u4E2A <code>ip</code> \u914D\u7F6E\u5BF9\u5E94\u7684 <code>\u57DF\u540D</code>\uFF0C\u7136\u540E\u6211\u4EEC\u76F4\u63A5\u901A\u8FC7\u8BBF\u95EE\u57DF\u540D\u7684\u65B9\u5F0F\uFF0C\u7531\u6D4F\u89C8\u5668\u53BB\u8FDB\u884C <code>DNS</code> \u89E3\u6790\u83B7\u53D6\u5230\u5BF9\u5E94\u7684 <code>ip</code> \u5730\u5740\uFF0C\u8FBE\u5230\u8BBF\u95EE\u76EE\u6807\u7684\u76EE\u7684\uFF08\u8FD9\u4E5F\u662F\u5E38\u89C4\u516B\u80A1\u6587 <a href="/interview/summary/index.html#_2-\u6D4F\u89C8\u5668\u8F93\u5165-url-\u53D1\u751F\u4E86\u4EC0\u4E48\uFF1F"><code>\u6D4F\u89C8\u5668\u8F93\u5165 URL \u53D1\u751F\u4E86\u4EC0\u4E48 \u{1F517}</code></a> \u7684\u7B2C\u4E00\u6B65 <code>DNS \u57DF\u540D\u89E3\u6790</code> \u505A\u7684\u4E8B\u60C5\uFF09</p><p><code>DNS \u52AB\u6301</code> \u53C8\u79F0\u4E3A <code>\u57DF\u540D\u52AB\u6301</code>\uFF0C\u5B83\u662F\u6307\u901A\u8FC7\u67D0\u4E9B\u624B\u6BB5\u83B7\u5F97\u67D0\u4E9B\u57DF\u540D\u7684\u89E3\u6790\u63A7\u5236\u6743\uFF0C\u5728\u4F60\u8BBF\u95EE\u67D0\u4E2A\u57DF\u540D\u65F6\uFF0C\u7ED9\u4F60\u8FD4\u56DE\u9519\u8BEF\u7684 <code>ip</code> \u5730\u5740\uFF0C\u7ED3\u679C\u5C31\u662F\u7F51\u5740\u65E0\u6CD5\u8BBF\u95EE\u6216\u8005\u5047\u7F51\u5740\uFF08\u6BD4\u5982\u653B\u51FB\u8005\u642D\u5EFA\u4E00\u4E2A\u8DDF\u539F\u7F51\u5740 <code>\u957F\u5F97\u4E00\u6837</code> \u7684\u9875\u9762\uFF0C\u4F60\u5728\u8FD9\u4E2A\u7F51\u7AD9\u7684\u4E00\u5207\u64CD\u4F5C\uFF0C\u6BD4\u5982\u767B\u5F55\u7684\u8D26\u53F7\u3001\u5BC6\u7801\u7B49\u4FE1\u606F\uFF0C\u53EF\u80FD\u90FD\u88AB\u5176\u8BB0\u5F55\u5E76\u53D1\u9001\u5230\u653B\u51FB\u8005\u624B\u91CC\u4E86\uFF09\u3002<code>DNS \u52AB\u6301</code> \u5E76\u4E0D\u662F\u8BF4 <code>\u9ED1\u6389\u4E86</code> \u5BF9\u65B9\u7684\u7F51\u7AD9\uFF0C\u800C\u662F\u4E00\u79CD <code>\u5192\u540D\u9876\u66FF</code> \u6216 <code>\u65E0\u54CD\u5E94</code> \u7684\u653B\u51FB\u65B9\u5F0F</p><p><strong>DNS \u52AB\u6301\u65B9\u6CD5\uFF1A</strong></p><ul><li><p>\u5229\u7528 DNS \u52AB\u6301\u8FDB\u884C <a href="https://help.aliyun.com/document_detail/28401.html" target="_blank" rel="noreferrer"><code>DDoS</code> \u653B\u51FB \u{1F517}</a></p><blockquote><p>\u7531\u4E8E\u5728 DNS \u7F13\u5B58\u672A\u547D\u4E2D\u7684\u60C5\u51B5\u4E0B\uFF0C\u4F1A\u5411 DNS \u670D\u52A1\u5668\u53D1\u8D77\u9012\u5F52\u67E5\u8BE2\uFF0C\u62FF\u5230\u6620\u5C04\u7ED3\u679C\u540E\u9010\u7EA7\u7F13\u5B58\u3002\u5047\u5982\u5728\u6B64\u8FC7\u7A0B\u4E2D\u653B\u51FB\u8005\u5DF2\u77E5\u6211\u4EEC\u7684 IP \u5730\u5740\uFF0C\u4E14\u653B\u51FB\u8005\u62E5\u6709\u8DB3\u591F\u6570\u91CF\u7684 <code>\u8089\u9E21</code>\uFF0C\u53CD\u590D\u7684\u8FDB\u884C\u5982\u4E0A\u64CD\u4F5C\uFF0C\u90A3\u4E48\u6211\u4EEC\u5C31\u4F1A\u53D7\u5230\u6765\u81EA\u4E8E DNS \u670D\u52A1\u5668\u7684\u54CD\u5E94\u4FE1\u606F DDoS \u653B\u51FB\uFF08\u7ED3\u679C\u5C31\u662F <code>\u6211\u4EEC\u7684\u7F51\u7EDC\u88AB\u62D6\u57AE\u81F3\u53D1\u751F\u4E2D\u65AD</code>\uFF09</p></blockquote></li><li><p>DNS \u7F13\u5B58\u611F\u67D3</p><blockquote><p>\u653B\u51FB\u8005\u4F7F\u7528 DNS \u8BF7\u6C42\uFF0C\u5C06\u6570\u636E\u653E\u5165\u4E00\u4E2A\u5177\u6709\u6F0F\u6D1E\u7684\u7684 DNS \u670D\u52A1\u5668\u7684\u7F13\u5B58\u5F53\u4E2D\uFF0C\u6211\u4EEC\u5728\u8FDB\u884C DNS \u8BBF\u95EE\u65F6\u62FF\u5230\u8FD9\u4E9B\u6C61\u67D3\u7684\u7F13\u5B58\u4FE1\u606F\uFF0C\u4ECE\u800C\u5C06\u6211\u4EEC\u6B63\u5E38\u7684\u57DF\u540D\u8BBF\u95EE <code>\u5BFC\u5411\u8BBF\u95EE\u653B\u51FB\u8005\u8BBE\u7F6E\u7684\u9493\u9C7C \u{1F3A3} \u7F51\u7AD9\uFF0C\u6216\u901A\u8FC7\u5176\u4ED6\u4F2A\u9020\u624B\u6BB5\u83B7\u53D6\u7528\u6237\u8D26\u53F7\u5BC6\u7801\u4FE1\u606F\u7B49\u7684\u653B\u51FB\u624B\u6BB5</code></p></blockquote></li><li><p>DNS \u4FE1\u606F\u52AB\u6301</p><blockquote><p>\u5047\u8BBE\u653B\u51FB\u8005\u5982\u679C\u901A\u8FC7\u76D1\u542C\u5BA2\u6237\u7AEF\u548C DNS \u670D\u52A1\u5668\u7684\u5BF9\u8BDD\uFF0C\u5728 DNS \u670D\u52A1\u5668\u4E4B\u524D\u5C06\u865A\u5047\u7684\u54CD\u5E94\u4EA4\u7ED9\u7528\u6237\uFF0C\u4ECE\u800C <code>\u6B3A\u9A97\u5BA2\u6237\u7AEF\u53BB\u8BBF\u95EE\u6076\u610F\u7684\u7F51\u7AD9</code></p></blockquote></li><li><p>DNS \u91CD\u5B9A\u5411</p><blockquote><p>\u653B\u51FB\u8005\u5C06 DNS \u540D\u79F0\u67E5\u8BE2\u91CD\u5B9A\u5411\u5230\u6076\u610F DNS \u670D\u52A1\u5668\u4E0A\uFF0C\u88AB\u52AB\u6301 <code>\u57DF\u540D\u7684\u89E3\u6790\u5C31\u5B8C\u5168\u5728\u653B\u51FB\u8005\u7684\u63A7\u5236\u4E4B\u4E0B</code></p></blockquote></li><li><p>ARP \u6B3A\u9A97</p><blockquote><p>ARP \u653B\u51FB\u5C31\u662F\u901A\u8FC7\u4F2A\u9020 IP \u5730\u5740\u548C MAC \u5730\u5740\u5B9E\u73B0 ARP \u6B3A\u9A97\uFF0C\u80FD\u591F\u5728\u7F51\u7EDC\u4E2D\u4EA7\u751F\u5927\u91CF\u7684 ARP \u901A\u4FE1\u91CF\u4F7F\u7F51\u7EDC\u963B\u585E\uFF0C<code>\u9020\u6210\u7F51\u7EDC\u4E2D\u65AD \u6216 \u4E2D\u95F4\u4EBA\u653B\u51FB</code></p></blockquote></li><li><p>\u672C\u673A\u52AB\u6301</p><blockquote><p>\u672C\u673A\u7684\u8BA1\u7B97\u673A\u7CFB\u7EDF\u88AB\u6728\u9A6C\u6216\u6D41\u6C13\u8F6F\u4EF6\u611F\u67D3\u540E\uFF0CDNS \u7F13\u5B58\u88AB\u4FEE\u6539\u5BFC\u81F4\u57DF\u540D\u8BBF\u95EE\u5F02\u5E38\u3002\u672C\u673A DNS \u52AB\u6301\u65B9\u5F0F\u5305\u62EC <code>hosts \u6587\u4EF6\u7BE1\u6539</code>\u3001<code>\u672C\u673A DNS \u52AB\u6301</code>\u3001<code>SPI \u94FE\u6CE8\u5165</code>\u3001<code>BHO \u63D2\u4EF6</code> \u7B49\u65B9\u5F0F</p></blockquote></li></ul><p><strong>\u9632\u6B62 DNS \u52AB\u6301\u65B9\u6CD5\uFF08\u7F51\u7EDC\u5C42\uFF09\uFF1A</strong></p><ul><li><p>\u51C6\u5907\u591A\u4E2A\u57DF\u540D\uFF0C\u53EF\u80FD\u51FA\u73B0\u4E00\u4E2A\u88AB\u653B\u51FB\u7684\u60C5\u51B5\u4E0B\uFF0C\u80FD\u591F\u6709\u5176\u4ED6\u57DF\u540D\u53EF\u4EE5\u6B63\u5E38\u8BBF\u95EE</p></li><li><p>\u624B\u52A8\u4FEE\u6539\u8DEF\u7531\u5668 <code>\u9996\u9009DNS</code> \u548C <code>\u5907\u7528DNS</code> IP</p><blockquote><ul><li>\u5730\u5740\u680F\u8F93\u5165 <code>192.168.0.1</code> / <code>192.168.1.1</code> \u767B\u5F55\u8FDB\u5165\u8DEF\u7531\u5668\u7BA1\u7406\u754C\u9762</li><li>\u5728 <code>DHCP\u670D\u52A1\u5668\u2014DHCP</code> \u670D\u52A1\u4E2D\uFF0C\u586B\u5199 <code>\u4E3B\u9009DNS\u670D\u52A1\u5668</code> \u4E3A\u66F4\u53EF\u9760\u7684 <code>114.114.114.114</code> \u5730\u5740\uFF0C<code>\u5907\u7528DNS\u670D\u52A1\u5668</code> \u4E3A <code>8.8.8.8</code></li><li>\u9694\u6BB5\u65F6\u95F4\u91CD\u65B0\u8BBE\u7F6E\u8DEF\u7531\u5668\u767B\u5F55\u5BC6\u7801</li></ul></blockquote></li></ul><h2 id="_6-\u8BF4\u8BF4-cors-\u8DE8\u7AD9\u811A\u672C\u653B\u51FB\u9632\u8303\uFF1F" tabindex="-1">6. \u8BF4\u8BF4 cors \u8DE8\u7AD9\u811A\u672C\u653B\u51FB\u9632\u8303\uFF1F <a class="header-anchor" href="#_6-\u8BF4\u8BF4-cors-\u8DE8\u7AD9\u811A\u672C\u653B\u51FB\u9632\u8303\uFF1F" aria-hidden="true">#</a></h2><p><code>CORS</code>\uFF1A\u8DE8\u57DF\u8D44\u6E90\u5171\u4EAB\uFF08<code>Cross-origin resource sharing</code>\uFF09\uFF0C\u5B83\u4F7F\u7528\u989D\u5916\u7684 <code>HTTP</code> \u5934\u6765\u544A\u8BC9\u6D4F\u89C8\u5668\u8BA9\u8FD0\u884C\u5728\u4E00\u4E2A <code>origin</code> (<code>domain</code>) \u4E0A\u7684 Web \u5E94\u7528\u88AB\u51C6\u8BB8\u8BBF\u95EE\u6765\u81EA\u4E0D\u540C\u6E90\u670D\u52A1\u5668\u4E0A\u7684\u6307\u5B9A\u7684\u8D44\u6E90</p><p>cors \u5C06\u8BF7\u6C42\u5206\u4E3A\u4E24\u79CD\uFF1A</p><ul><li><code>\u7B80\u5355\u8BF7\u6C42\uFF08simple request\uFF09</code></li><li><code>\u975E\u7B80\u5355\u8BF7\u6C42\uFF08preflight request\uFF09</code>\uFF0C\u4F1A\u9996\u5148\u53D1\u9001\u4E00\u4E2A <code>OPTIONS</code> \u9884\u68C0\u8BF7\u6C42</li></ul><p><strong>\u7B80\u5355\u8BF7\u6C42</strong> \u9700\u8981\u6EE1\u8DB3\u4EE5\u4E0B\u6761\u4EF6\uFF1A</p><ul><li>\u8BF7\u6C42\u7684\u65B9\u6CD5\u53EA\u80FD\u662F <code>GET</code>, <code>POST</code>, <code>HEAD</code> \u7684\u4E00\u79CD</li><li>\u8BF7\u6C42\u7684 <code>header</code> \u7684\u53EA\u80FD\u662F <code>Accept</code>\uFF0C<code>Accept-Language</code>, <code>Content-Language</code>\uFF0C<code>Content-Type</code> \u8FD9\u4E9B\u5B57\u6BB5\uFF0C\u4E0D\u80FD\u8D85\u51FA\u8FD9\u4E9B\u5B57\u6BB5</li><li><code>Content-Type</code> \u5B57\u6BB5\uFF0C\u53EA\u80FD\u662F\u4EE5\u4E0B\u503C\uFF1A <ul><li><code>text/plain</code></li><li><code>multipart/form-data</code></li><li><code>application/x-www-form-urlencoded</code></li></ul></li></ul><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u5747\u662F\u975E\u7B80\u5355\u8BF7\u6C42</p><p>\u975E\u7B80\u5355\u8BF7\u6C42\uFF0C\u5148\u53D1\u51FA\u9884\u68C0\u8BF7\u6C42\uFF0C\u68C0\u67E5\u5F53\u524D\u8BF7\u6C42\u662F\u5426\u7B26\u5408\u670D\u52A1\u5668\u7684 cors \u914D\u7F6E\uFF0C\u5982\u679C\u7B26\u5408\uFF0C\u5219\u518D\u53D1\u51FA\u771F\u6B63\u7684\u8BF7\u6C42\uFF1B\u4E0D\u7B26\u5408\u5219\u76F4\u63A5\u8FD4\u56DE\u8DE8\u57DF</p><blockquote><p>\u5982\u679C\u9884\u68C0\u8BF7\u6C42\u901A\u8FC7\u4E86\uFF0C\u5728\u4E00\u5B9A\u65F6\u95F4\u5185\u91CD\u590D\u8BF7\u6C42\u662F\u4E0D\u7528\u518D\u6B21\u53D1\u8D77\u9884\u68C0\u8BF7\u6C42</p></blockquote><h2 id="_7-\u8BF4\u8BF4-csrf-\u8DE8\u7AD9\u8BF7\u6C42\u4F2A\u9020\u9632\u8303\uFF1F" tabindex="-1">7. \u8BF4\u8BF4 CSRF \u8DE8\u7AD9\u8BF7\u6C42\u4F2A\u9020\u9632\u8303\uFF1F <a class="header-anchor" href="#_7-\u8BF4\u8BF4-csrf-\u8DE8\u7AD9\u8BF7\u6C42\u4F2A\u9020\u9632\u8303\uFF1F" aria-hidden="true">#</a></h2><h2 id="_8-devops\uFF08\u5982-jenkins-\u7B49\uFF09" tabindex="-1">8. devOps\uFF08\u5982 jenkins \u7B49\uFF09 <a class="header-anchor" href="#_8-devops\uFF08\u5982-jenkins-\u7B49\uFF09" aria-hidden="true">#</a></h2><h2 id="_9-\u6D4B\u8BD5\u6846\u67B6\uFF08jest\uFF09" tabindex="-1">9. \u6D4B\u8BD5\u6846\u67B6\uFF08jest\uFF09 <a class="header-anchor" href="#_9-\u6D4B\u8BD5\u6846\u67B6\uFF08jest\uFF09" aria-hidden="true">#</a></h2><h2 id="_10-\u8BF4\u4E00\u8BF4-qiankun-\u7684\u8DEF\u7531\u5339\u914D\u539F\u7406" tabindex="-1">10. \u8BF4\u4E00\u8BF4 <code>qiankun</code> \u7684\u8DEF\u7531\u5339\u914D\u539F\u7406 <a class="header-anchor" href="#_10-\u8BF4\u4E00\u8BF4-qiankun-\u7684\u8DEF\u7531\u5339\u914D\u539F\u7406" aria-hidden="true">#</a></h2>',41),t=[l];function a(r,s,p,n,u,h){return c(),o("div",null,t)}const _=e(i,[["render",a]]);export{S as __pageData,_ as default};
