import{_ as e,c as a,e as t,d as o,o as p}from"./app.00347171.js";const l='{"title":"\u5178\u7C4D\u91CC\u7684\u4E2D\u56FD","description":"","frontmatter":{"title":"\u5178\u7C4D\u91CC\u7684\u4E2D\u56FD"},"relativePath":"mood/chinaInTheClassics.md","lastUpdated":1654809095713}',r={},c=o('<h4 id="\xA7-\u5F00\u7BC7" tabindex="-1">\xA7 \u5F00\u7BC7 <a class="header-anchor" href="#\xA7-\u5F00\u7BC7" aria-hidden="true">#</a></h4><p>\u60DF\u6BB7\u5148\u4EBA\uFF0C\u6709\u518C\u6709\u5178 \u2014\u2014\u300A\u5C1A\u4E66 \xB7 \u591A\u58EB\u300B</p><p>\u51E0\u5343\u5E74\u6765\uFF0C\u7956\u5148\u4E00\u76F4\u5728\u8BB0\u5F55\u6211\u4EEC\u7684\u5386\u53F2\uFF0C\u8BB2\u8FF0\u6211\u4EEC\u7684\u6545\u4E8B\u3002\u8FD9\u91CC\u7684\u6BCF\u4E00\u90E8\u5178\u7C4D\uFF0C\u90FD\u51DD\u805A\u7740\u524D\u4EBA\u7684\u5FC3\u8840\u548C\u667A\u6167\uFF0C\u4EBA\u4EEC\u4E16\u4EE3\u5B88\u62A4\uFF0C\u85AA\u706B\u76F8\u4F20\u3002\u8BA9\u7CBE\u795E\u7684\u8840\u8109\u5EF6\u7EF5\u81F3\u4ECA\u3002\u6253\u5F00\u5178\u7C4D\uFF0C\u5BF9\u8BDD\u5148\u8D24\uFF0C\u77E5\u9053\u6211\u4EEC\u7684\u751F\u547D\u7F18\u8D77\u4F55\u5904\uFF0C\u77E5\u9053\u6211\u4EEC\u7684\u811A\u6B65\u8FC8\u5411\u4F55\u65B9\u3002\u4EE5\u65B0\u7684\u65B9\u5F0F\u8BFB\u61C2\u5178\u7C4D\uFF0C\u8BA9\u4E66\u5199\u5728\u53E4\u7C4D\u91CC\u7684\u6587\u5B57\u6D3B\u8D77\u6765\uFF0C\u4E00\u8D77\u54C1\u8BFB~\u300A\u5178\u7C4D\u91CC\u7684\u4E2D\u56FD\u300B\u3002</p><h4 id="\xA7-\u7B80\u4ECB" tabindex="-1">\xA7 \u7B80\u4ECB <a class="header-anchor" href="#\xA7-\u7B80\u4ECB" aria-hidden="true">#</a></h4><p>2021 \u5E74\uFF0C\u592E\u89C6\u63A8\u51FA\u5927\u578B\u6587\u5316\u7C7B\u8282\u76EE \u2014\u2014 \u300A\u5178\u7C4D\u91CC\u7684\u4E2D\u56FD\u300B</p><p>\u901A\u8FC7\u805A\u7126\u4F18\u79C0\u4E2D\u534E\u6587\u5316\u5178\u7C4D\uFF0C\u4EE5\u6C89\u6D78\u5F0F\u620F\u5267\u548C\u53E4\u4ECA\u5BF9\u8BDD\u7684\u65B9\u5F0F\uFF0C\u4E3A\u5927\u5BB6\u8BB2\u8FF0\u5178\u7C4D\u6545\u4E8B\uFF0C\u5BF9\u8BDD\u534E\u590F\u5148\u8D24\uFF0C\u4EE5\u6B64\u5E26\u9886\u89C2\u4F17\u6DF1\u5165\u611F\u53D7\u90A3\u4E9B\u6D41\u4F20\u5343\u5E74\u7684\u5178\u7C4D\u91CC\u7684\u6545\u4E8B\u3002</p><h4 id="\xA7-\u89C2\u611F" tabindex="-1">\xA7 \u89C2\u611F <a class="header-anchor" href="#\xA7-\u89C2\u611F" aria-hidden="true">#</a></h4><p>\u6B64\u8282\u76EE\u81EA\u4ECA\u5E74 2 \u6708 12 \u65E5\u63A8\u51FA\u81F3\u4ECA\uFF0C\u4EC5\u51FA\u4E86 5 \u671F\uFF0C\u5927\u6982\u66F4\u65B0\u9891\u7387\u4E3A\u6708\u66F4\u3002\u6BD5\u7ADF\u4F18\u79C0\u7684\u4E1C\u897F\u90FD\u662F\u9700\u8981\u6C89\u6DC0\u548C\u6253\u78E8\u7684\uFF0C\u4E0D\u8BBA\u662F\u4ECE\u8BB2\u8FF0\u7684\u5178\u7C4D\uFF0C\u8BDD\u5267\u7684\u8868\u6F14\u8005\uFF0C\u8FD8\u662F\u6210\u578B\u7684\u5267\u672C\uFF0C\u6BCF\u4E00\u4E2A\u9009\u62E9\u4E0E\u8C03\u6574\u90FD\u662F\u614E\u91CD\u4E14\u7528\u5FC3\u7684\u3002</p><p>\u6BD4\u8F83\u89E6\u52A8\u6211\u7684\u662F\u620F\u5267\u8868\u6F14\u4E4B\u524D\u7684\u4E00\u4E2A\u7ECF\u5178\u7247\u6BB5\uFF1A\u90A3\u5C31\u662F\u8868\u6F14\u8005\u8EAB\u7740\u73B0\u4EE3\u670D\u88C5\uFF0C\u901A\u8FC7\u955C\u5B50\u4E0E\u8EAB\u7740\u53E4\u88C5\u7684\u89D2\u8272\u4E92\u76F8\u5BF9\u89C6\u3001\u884C\u793C\uFF0C\u4ED6\u4EEC\u773C\u795E\u91CC\u6D41\u9732\u7684\u60C5\u611F\u603B\u662F\u80FD\u8BA9\u4EBA\u89C9\u5F97\u8FD9\u624D\u662F\u771F\u7684\u6F14\u5458\uFF01\u56E0\u4E3A\u6709\u90A3\u4E48\u4E00\u77AC\u95F4\u8BA9\u6211\u89C9\u5F97\u955C\u5B50\u91CC\u5BF9\u89C6\u7684\u4EBA\u5C31\u662F\u5343\u5E74\u4E4B\u524D\u7684\u89D2\u8272\u3002</p><p>\u300A\u5178\u7C4D\u91CC\u7684\u4E2D\u56FD\u300B\u9009\u62E9\u300A\u5C1A\u4E66\u300B\u4F5C\u4E3A\u8282\u76EE\u7684 &quot;\u5F00\u7BC7&quot;\uFF0C\u662F\u56E0\u4E3A\u300A\u5C1A\u4E66\u300B\u6D41\u4F20\u4E86\u4E24\u5343\u591A\u5E74\uFF0C\u7D20\u6709 &quot;\u653F\u4E66\u4E4B\u7956\uFF0C\u53F2\u4E66\u4E4B\u6E90&quot; \u7684\u7F8E\u8A89\u3002\u540E\u51E0\u671F\u5206\u522B\u6F14\u7ECE\u5E76\u8BB2\u8FF0\u4E86\u300A\u5929\u5DE5\u5F00\u7269\u300B\u3001\u300A\u53F2\u8BB0\u300B\u3001\u300A\u672C\u8349\u7EB2\u76EE\u300B\u3001\u300A\u8BBA\u8BED\u300B\u7684\u76F8\u5173\u60C5\u8282\u6545\u4E8B\uFF0C\u6BCF\u4E00\u90E8\u7684\u89D2\u8272\u90FD\u8BA9\u4EBA\u5370\u8C61\u6DF1\u523B\uFF0C\u8001\u6F14\u5458\u7684\u6F14\u7ECE\u80FD\u529B\u4E0E\u5171\u60C5\u611F\uFF0C\u90FD\u662F\u5F88\u5F3A\u7684\u50AC\u6CEA &quot;\u70B8\u5F39&quot;(\u81F3\u5C11\u6211\u6709\u51E0\u6B21\u6CA1\u5FCD\u4F4F\uFF0C\u6CEA\u6C34\u5728\u6253\u8F6C\u4E86)\uFF0C\u76F8\u4FE1\u770B\u8FC7\u7684\u4EBA\u5E94\u8BE5\u4F1A\u6709\u540C\u611F\u3002</p><p>\u620F\u5267\u6F14\u7ECE\u7684\u6545\u4E8B\u60C5\u8282\u4E00\u822C\u90FD\u662F\u9009\u62E9\u4E3B\u8981\u89D2\u8272\u7684\u4EBA\u751F\u5386\u7A0B\u4E3A\u4E3B\u7EBF\u8FDB\u884C\u6F14\u7ECE\uFF0C\u5206\u522B\u9970\u6F14\u4E2D\u5E74\u3001\u8001\u5E74\u7684\u5267\u60C5\uFF0C\u6BCF\u6B21\u770B\u5230\u8001\u5E74\u7684\u89D2\u8272\u65F6\uFF0C\u903C\u771F\u7684\u5986\u5BB9\u4E0E\u60C5\u611F\u8868\u8FBE\u7684\u7ED3\u5408\u603B\u662F\u50AC\u4EBA\u6CEA\u4E0B\u3002</p><p>\u5230\u5E95\u662F &quot;\u56FD\u5BB6\u961F&quot;\uFF0C\u5B9E\u529B\u5C31\u662F\u4E0D\u4E00\u6837\u54C8\uFF01\u54C8\u54C8\u54C8~~</p><p>\u867D\u7136\u5E73\u5E38\u65E0\u804A\u7684\u65F6\u5019\u4E5F\u4F1A\u770B\u5176\u4ED6\u7684\u7EFC\u827A\u8282\u76EE(\u5F53\u7136\u7EAF\u7CB9\u662F\u4E3A\u4E86\u5F00\u5FC3)\uFF0C\u4F46\u300A\u5178\u7C4D\u91CC\u7684\u4E2D\u56FD\u300B\u7EDD\u5BF9\u662F\u6700\u671F\u5F85\u7684\uFF0C\u4E00\u662F\u56E0\u4E3A\u5B83\u8282\u76EE\u672C\u8EAB\u4E00\u79CD\u5168\u65B0\u7684\u821E\u53F0\u5F0F\u620F\u5267\u4E0E\u53E4\u4ECA\u5BF9\u8BDD\u7684\u65B9\u5F0F\u8DB3\u591F\u5F15\u4EBA\u5165\u80DC\uFF1B\u4E8C\u5C31\u662F\u5B83\u4EE5\u4E00\u79CD\u65B0\u7684\u65B9\u5F0F\u5E2E\u52A9\u5927\u5BB6\u53BB\u8BC6\u8BFB\u53EF\u80FD\u89C9\u5F97\u6666\u6DA9\u96BE\u61C2\u7684\u5178\u7C4D\uFF1B\u4E09\u5C31\u662F\u5B83\u4F20\u627F\u7684\u7CBE\u795E\u5C42\u9762\u7684\u4E1C\u897F\uFF0C\u534E\u590F\u6587\u660E\u7684\u5386\u53F2\u4F20\u627F\u8981\u9760\u6211\u4EEC\uFF0C\u901A\u8FC7\u8BFB\u4E66\u8BA9\u8FD9\u4E9B\u6545\u4E8B\u4EE3\u4EE3\u76F8\u4F20\u3002\u6709\u7F51\u53CB\u79F0\u8D5E\u5B83\uFF1A\u582A\u79F0\u662F\u827A\u672F\u548C\u6587\u5316\u7684\u9955\u992E\u76DB\u5BB4\u3002</p><p>\u867D\u7136\u53EF\u80FD\u4E0A\u5347\u7684\u9AD8\u5EA6\u6709\u70B9\u9AD8\uFF0C\u4F46\u662F\u6211\u89C9\u5F97\u8FD9\u7C7B\u8282\u76EE\u786E\u5B9E\u633A\u597D\u7684\uFF0C\u6BD5\u7ADF\u4E0D\u50CF\u5F53\u4ECA\u793E\u4F1A\u7684\u5FEB\u9910\u6587\u5316\u3001\u65E0\u8111\u5BA3\u4F20\u4E0E &quot;\u660E\u661F&quot; \u4E4B\u7C7B\u7684\u4E1C\u897F\uFF0C\u54C1\u8BFB\u5178\u7C4D\u66F4\u80FD\u5F15\u53D1\u4EBA\u4EEC\u7684\u601D\u8003\u4E0E\u4E2A\u4EBA\u9053\u5FB7\u54C1\u8D28\u7684\u63D0\u9AD8\uFF0C\u63D0\u5347\u7684\u662F\u6574\u4E2A\u4EBA\u7684\u601D\u60F3\u5883\u754C\u4E0E\u4E2A\u4EBA\u6DB5\u517B...</p><p>\u55EF\uFF0C\u63A8\u8350\u5927\u5BB6\u53BB\u770B\u770B\u5427~</p><blockquote><p>BGM: <a href="https://webfs.yun.kugou.com/202106170130/902bd55debf26c8f84b80bd7463b414d/KGTX/CLTX001/8aacfa8cc17f8a75c7df1fc38bdfc45b.mp3" target="_blank" rel="noopener noreferrer">\u300A\u706F\u706B\u91CC\u7684\u4E2D\u56FD (\u94A2\u7434\u72EC\u594F)\u300B</a></p></blockquote>',16);function d(n,s,i,h,_,u){return p(),a("div",null,[t(" # \u5178\u7C4D\u91CC\u7684\u4E2D\u56FD "),c])}var b=e(r,[["render",d]]);export{l as __pageData,b as default};
