import{_ as e,c as t,o,b as a}from"./app.7e4dd5a1.js";const m=JSON.parse('{"title":"\u8981\u4E48\u5B64\u72EC\uFF0C\u8981\u4E48\u5EB8\u4FD7","description":"","frontmatter":{"title":"\u8981\u4E48\u5B64\u72EC\uFF0C\u8981\u4E48\u5EB8\u4FD7"},"headers":[{"level":2,"title":"01 \uFE34","slug":"_01-\uFE34"},{"level":2,"title":"02 \uFE34","slug":"_02-\uFE34"}],"relativePath":"mood/loneliness.md"}'),s={name:"mood/loneliness.md"},p=a('<p>\u4F60\u597D\uFF0C\u964C\u751F\u4EBA\uFF0C\u5F88\u9AD8\u5174\u80FD\u5728\u8FD9\u91CC\u4E0E\u4F60\u76F8\u9047\uFF01</p><h2 id="_01-\uFE34" tabindex="-1">01 \uFE34 <a class="header-anchor" href="#_01-\uFE34" aria-hidden="true">#</a></h2><p>\u53D4\u672C\u534E\u8BF4\uFF1A</p><blockquote><p>\u8981\u4E48\u5B64\u72EC\uFF0C\u8981\u4E48\u5EB8\u4FD7</p></blockquote><p>\u8FD9\u53E5\u8BDD\u4E8E\u6211\u800C\u8A00\u597D\u4F3C\u89E6\u52A8\u4E86\u4E00\u4E9B\u4EC0\u4E48\uFF1F\uFF01\u8BF4\u4E0D\u6E05\uFF0C\u4E5F\u9053\u4E0D\u660E\u2026\u2026</p><p>\u9A6C\u5C14\u514B\u65AF\u5728\u300A\u767E\u5E74\u5B64\u72EC\u300B\u4E2D\u6709\u8A00\uFF1A\u201C\u5B64\u72EC\u662F\u4E00\u79CD\u7231\u7684\u80FD\u529B\u7684\u7F3A\u5931\u201D\u3002\u4F46\u540C\u65F6\uFF0C\u5B64\u72EC\u4E3A\u4E00\u4E2A\u7CBE\u795E\u7980\u8D4B\u4F18\u5F02\u7684\u4EBA\u4F1A\u5E26\u6765\u53CC\u91CD\u7684\u597D\u5904\uFF1A\u7B2C\u4E00\uFF0C\u4ED6\u53EF\u4EE5\u4E0E\u81EA\u5DF1\u4E3A\u4F34\uFF1B\u7B2C\u4E8C\uFF0C\u4ED6\u7528\u4E0D\u7740\u548C\u522B\u4EBA\u5728\u4E00\u8D77\u3002\u7B2C\u4E8C\u70B9\u5F25\u8DB3\u73CD\u8D35\uFF0C\u5C24\u5176\u6211\u4EEC\u8FD8\u8BB0\u5F97\u793E\u4F1A\u4EA4\u5F80\u6240\u610F\u5473\u7740\u7684\u675F\u7F1A\u3001\u70E6\u6270\u751A\u81F3\u5371\u9669\uFF0C\u62C9\u5E03\u53F6\u8BF4\u8FC7\uFF1A\u201C\u6211\u4EEC\u627F\u53D7\u6240\u6709\u4E0D\u5E78\u7686\u56E0\u6211\u4EEC\u65E0\u6CD5\u72EC\u5904\u3002\u201D \u4E00\u822C\u800C\u8A00\uFF0C\u4E00\u4E2A\u4EBA\u5BF9\u793E\u4F1A\u4EA4\u5F80\u7684\u6E34\u671B\u7A0B\u5EA6\u4E0E\u4ED6\u7684\u5E74\u9F84\u5927\u5C0F\u6210\u53CD\u6BD4\u3002\u5E74\u5E7C\u7684\u5C0F\u5B69\u72EC\u81EA\u5446\u4E0A\u4E00\u4F1A\u513F\u7684\u65F6\u95F4\u5C31\u4F1A\u611F\u5230\u60CA\u6050\u548C\u54ED\u558A\uFF0C\u4F46\u5F53\u6211\u4EEC\u9010\u6E10\u957F\u5927\u4E4B\u540E\uFF0C\u5F80\u5F80\u66F4\u559C\u6B22\u9009\u62E9\u8FDC\u79BB\u793E\u4EA4\uFF08\u8FD9\u53EA\u662F\u4EE5\u672C\u4EBA\u81EA\u8EAB\u7684\u89D2\u5EA6\u6765\u8BF4\uFF09\u3002</p><p>\u67CF\u90A3\u767B\xB7\u5FB7\xB7\u5723\u6BD4\u57C3\u7684\u8BDD\u4E00\u8BED\u4E2D\u7684\uFF0C\u5E76\u4E14\u8BF4\u5F97\u5F88\u7F8E\u5999\uFF1A\u201C\u8282\u5236\u4E0E\u4EBA\u4EA4\u5F80\u4F1A\u4F7F\u6211\u4EEC\u5FC3\u7075\u5E73\u9759\u3002\u201D \u4EBA\u9645\u4EA4\u5F80\uFF0C\u4E0D\u8BBA\u662F\u8EAB\u4EFD\u5BF9\u7B49\u4E4B\u4EBA\u4E4B\u95F4\uFF0C\u6216\u662F\u56E0\u4E3A\u94B1\u8D22\u3001\u5B98\u804C\u3001\u5DE5\u4F5C\u4E0A\u4E0B\u7EA7\u7B49\u9644\u5C5E\u7269\u800C\u4EA7\u751F\u7684\u9636\u7EA7\u4E4B\u95F4\uFF0C\u603B\u662F\u4F1A\u6709\u7740\u4E00\u4E9B\u4E1C\u897F\u8426\u7ED5\u5728\u6211\u4EEC\u5FC3\u5934\uFF0C\u6216\u8BB8\u662F\u4F60\u89C9\u5F97\u4E0D\u80FD\u4E8F\u6B20\u4E86\u4EBA\u5BB6\uFF0C\u4E0D\u8BBA\u662F\u4EA4\u60C5\u3001\u611F\u60C5\u8FD8\u662F\u5E94\u8BE5 AA \u7684\u996D\u94B1\uFF08\u5F53\u7136\uFF0C\u4ED6\u4EEC\u53EF\u80FD\u4E0D\u662F\u8FD9\u4E48\u60F3\uFF0C\u6BD4\u5982\u4F60\u60F3 AA \u7684\u996D\u94B1\uFF0C\u4ED6\u53EF\u80FD\u89C9\u5F97\u5C31\u8FD9\u767E\u5341\u6765\u5757\u94B1\uFF0C\u4F60\u8FD8\u5F97\u8DDF\u6211\u5E73\u644A\uFF0C\u660E\u663E\u4E0D\u628A\u6211\u5F53\u670B\u53CB\uFF09\uFF0C\u4E5F\u6216\u8BB8\u662F\u4F60\u89C9\u5F97\u56E0\u4E3A\u4EBA\u5BB6 \u201C\u8EAB\u4EFD\u5C0A\u8D35\u201D \u800C\u4E0D\u5F97\u4E0D\u8FCE\u5408\u4ED6\u2026\u2026 \u8FD9\u4E9B\u4E1C\u897F\u603B\u662F\u5728\u6B64\u8FC7\u7A0B\u4E2D\u8BA9\u81EA\u5DF1\u53D8\u5F97 \u201C\u6218\u6218\u5162\u5162\u201D\u3001\u201C\u552F\u552F\u8BFA\u8BFA\u201D\u2026\u2026</p><h2 id="_02-\uFE34" tabindex="-1">02 \uFE34 <a class="header-anchor" href="#_02-\uFE34" aria-hidden="true">#</a></h2><p>\u5B64\u72EC\u4E0E\u5EB8\u4FD7\uFF0C\u8FD9\u4E24\u4E2A\u8BCD\u4E00\u542C\u597D\u50CF\u90FD\u662F\u9971\u542B\u8D2C\u4E49\u7684\u542B\u4E49\u3002\u4F46\u4F60\u53EF\u4EE5\u6362\u4E2A\u89D2\u5EA6\uFF0C\u6BD4\u5982\u8FD9\u4E48\u7406\u89E3\uFF1A</p><p><code>\u5B64\u72EC</code> \u4F5C\u4E3A\u5FC3\u7406\u611F\u5B98\uFF08\u59D1\u4E14\u8BA9\u6211\u8FD9\u4E48\u53EB\uFF09\u6765\u8BB2\uFF0C\u53EF\u4EE5\u8BA4\u4E3A\u662F\u4F60\u4E3B\u89C2\u4E0A\u8BA4\u8BC6\u5230\u4EA7\u751F\u4E86\u5B64\u72EC\u611F\uFF0C\u5C31\u4F1A\u60F3\u8981\u53BB\u505A\u4E9B\u4E8B\u6765\u6392\u89E3\u81EA\u5DF1\u5FC3\u91CC\u7684\u8FD9\u4EFD\u611F\u89C9\uFF0C\u8FD9\u4E5F\u662F\u5FC3\u7406\u4E0A\u5BF9\u81EA\u5DF1\u53D1\u51FA\u7684\u8B66\u544A\u4FE1\u606F\u4E0D\u662F\u5417\uFF1F\u90A3\u4E48\u4F60\u5C31\u53EF\u4EE5\u9009\u62E9\u5C06\u5B83\u5F53\u4F5C\u505A\u4E00\u4E9B\u4E8B\u60C5\u7684\u5F00\u7AEF\uFF0C\u7136\u540E\u5F00\u542F\u65B0\u7684\u4EBA\u751F\u5386\u7A0B\uFF08\u524D\u63D0\u9700\u8981\u4F60\u7684\u5FC3\u7406\u662F\u79EF\u6781\u7684\uFF09\u3002\u800C\u53E6\u4E00\u65B9\u9762\u5BF9\u62B1\u4EE5\u7C7B\u4F3C \u201C\u6D88\u6781\u907F\u4E16\u201D \u5FC3\u6001\u7684\u4EBA\u6765\u8BF4\uFF0C\u4ED6\u4EEC\u5F80\u5F80\u66F4\u559C\u6B22\u72EC\u5904\uFF0C\u8BA8\u538C\u4EBA\u591A\u70ED\u95F9\u7684\u573A\u5408\u3002\u4F46\u6709\u65F6\u5019\u5374\u56E0\u4E3A\u4E00\u4E9B\u539F\u56E0\u4E0D\u5F97\u4E0D\u5230\u573A\uFF08\u9022\u573A\u4F5C\u620F\uFF1F\uFF09\uFF0C\u5728\u4EBA\u9645\u4EA4\u5F80\u65F6\u4ED6\u4F1A\u8868\u73B0\u5730\u4E00\u5207\u5982\u5E38\uFF0C\u4F46\u8A00\u8BED\u4E0E\u4E3E\u6B62\u4E4B\u95F4\u4F60\u603B\u80FD\u611F\u53D7\u5230\u4ED6\u7684\u683C\u683C\u4E0D\u5165\u611F\u2026\u2026</p><p><code>\u5EB8\u4FD7</code> \u8FD9\u4E2A\u8BCD\u57FA\u672C\u7528\u4F5C\u8D2C\u4E49\uFF0C\u664B \u845B\u6D2A \u300A\u62B1\u6734\u5B50\xB7\u7A77\u8FBE\u300B\u6709\u4E66\uFF1A\u5EB8\u4FD7\u4E4B\u592B\uFF0C\u95C7\uFF08\xE0n\uFF09\u65BC\uFF08y\xFA\uFF09\u522B\u7269\uFF0C\u4E0D\u5206\u6731\u7D2B\uFF0C\u4E0D\u8FA9\u83FD\uFF08sh\u016B\uFF09\u9EA6\u3002\u4EE5\u989C\u8272\u4E0D\u5206\uFF0C\u8C46\u9EA6\u4E0D\u8FA8\u4E3A\u4F50\u8BC1\u4E4B\u8A00\uFF0C\u5C1A\u53EF\u77E5\u5176\u8D2C\u4F4E\u4E4B\u610F\u3002\u4F46\u6211\u60F3\uFF0C\u3010\u8981\u4E48\u5B64\u72EC\uFF0C\u8981\u4E48\u5EB8\u4FD7\u3011\u4E4B\u4E2D\u7684\u5EB8\u4FD7\uFF0C\u5927\u62B5\u662F\u5E73\u5EB8\u4E4B\u610F\u66F4\u591A\u5427\uFF0C\u5E94\u4E0D\u81F3\u4E8E\u53D6\u5176\u7C97\u4FD7\u4E4B\u610F\uFF0C\u8FD9\u91CC\u8FD9\u4E48\u8BF4\u5E94\u8BE5\u662F\u4E3A\u4E86\u62BC\u97F5\u4E14\u6717\u6717\u4E0A\u53E3\u3002</p><p>\u4EE5\u4E0A\uFF0C\u6211\u8BA4\u4E3A\u5B64\u72EC\u4E0E\u5EB8\u4FD7\u5C31\u81EA\u7136\u800C\u7136\u5730\u5F62\u6210\u4E86\u4E00\u4E2A\u76F8\u5BF9\u7ACB\u7684\u5904\u4E8B\u6001\u5EA6\u4E0E\u7ED3\u679C\u3002\u3010\u5B64\u72EC\u3011\u80FD\u8BA9\u6211\u6709\u66F4\u8212\u9002\u7684\u5FC3\u7406\u611F\u53D7\uFF0C\u80FD\u66F4\u5E73\u9759\u5730\u9009\u62E9\u53BB\u505A\u67D0\u4EF6\u4E8B\uFF0C\u800C\u4E0D\u7528\u7ED9\u81EA\u5DF1\u589E\u6DFB\u5F88\u591A\u4E0D\u5FC5\u8981\u7684\u5FC3\u7406\u8D1F\u62C5\uFF0C\u5B66\u4F1A\u4FE1\u4EFB\u81EA\u5DF1\uFF0C\u4E60\u60EF\u72EC\u81EA\u884C\u52A8\u3002\u3010\u5EB8\u4FD7\u3011\u5219\u50CF\u662F\u81EA\u5F03\u8005\u9009\u62E9\u63A5\u53D7\u81EA\u5DF1\u7684\u5E73\u5EB8\u4E14\u653E\u5F03\u524D\u8FDB\u7684\u6B65\u4F10\uFF0C\u9009\u62E9\u4E0D\u4F5C\u4E3A\u7684\u4EBA\u751F\u3002</p><p>\u6700\u540E\uFF0C\u9001\u7ED9\u5927\u5BB6\u4E00\u53E5\u8BDD\uFF1A</p><div class="tip custom-block"><p class="custom-block-title">wishes</p><p>\u5B64\u72EC\u662F\u56F0\u82E6\u7684\uFF1B\u4F46\u53EF\u4E0D\u8981\u53D8\u5F97\u5EB8\u4FD7\uFF1B\u56E0\u4E3A\u8FD9\u6837\uFF0C\u4F60\u5C31\u4F1A\u53D1\u73B0\u5230\u5904\u90FD\u662F\u4E00\u7247\u6C99\u6F20\u3002</p></div>',14),c=[p];function _(l,i,d,n,r,h){return o(),t("div",null,c)}var f=e(s,[["render",_]]);export{m as __pageData,f as default};
